-- all interface functions and allowed globals used by the game.-- some of these are not used at all, though.local INTERFACE_FUNCTIONS ={    action = true,    activate = true,    animationKey = true,    castSong = true,    damage = true,    dieEaten = true,    dieNormal = true,    enterState = true,    entityDied = true,    exitState = true,    exitTimer = true,    hitEntity = true,    hitSurface = true,    init = true,    lightFlare = true,    msg = true,    postInit = true,    preUpdate = true,    shiftWorlds = true,    shotHitEntity = true,    song = true,    songNote = true,    songNoteDone = true,    sporesDropped = true,    update = true,    useTreasure = true,        deathNotify = true,        -- custom ones [TODO] ?}local function isInterfaceFunction(s)    return INTERFACE_FUNCTIONS[s]endreturn {    isInterfaceFunction = isInterfaceFunction}