function init(me)    setupEntity(me, "ingredients/rainbow-mushroom")    entity_setEntityType(me, ET_NEUTRAL)    entity_setWeight(me, 500)    entity_setMaxSpeed(me, 1000)    entity_rotate(me, -360, 3, -1)    entity_scale(me, 0.6, 0.6)endfunction update(me, dt)    entity_updateMovement(me, dt)endfunction postInit() endfunction damage() return false endfunction enterState() endfunction exitState() endfunction msg() endfunction animationKey() endfunction song() endfunction songNoteDone() endfunction songNote() endfunction shiftWorlds() endfunction lightFlare() endfunction hitSurface() end