-- interval (default 2), initDelay, velX, velY (default -1200), weight (default 1400),v.t = 0v.timer = 2v.initDelay = 0v.velx = 0v.vely = -1200v.weight = falsefunction init(me)    local a = node_getName(me):explode(" ", true)    v.timer = tonumber(a[2]) or 2    v.initDelay = tonumber(a[3]) or 0    v.velx = tonumber(a[4]) or 0    v.vely = tonumber(a[5]) or -1200    v.weight = tonumber(a[6]) or false        v.t = v.initDelay        -- for the balls    loadSound("sizzle")endfunction update(me, dt)    if v.t >= 0 then        v.t = v.t - dt        if v.t <= 0 then            local e = createEntity("lavaball", "", node_getPosition(me))            entity_addVel(e, v.velx, v.vely)            v.t = v.t + v.timer            if v.weight then                entity_setWeight(e, v.weight)            end        end    end    endfunction activate(me)    v.t = v.initDelayendfunction songNote(me, note)endfunction songNoteDone(me, note, done)end