dofile("scripts/inc_mapsave.lua")local function canOpen()    return v.hasBeatenMap("gravity")       and v.hasBeatenMap("upisdown")endfunction init(me)    if canOpen() then        local door = node_getNearestEntity(me, "energydoor")        if door == 0 then            errorLog("node_openlastdoor2.lua: No door found!")            return        end        entity_setState(door, STATE_OPEN)    endendfunction update(me, dt)endfunction activate(me)endfunction songNote(me, note)endfunction songNoteDone(me, note, done)end