if not v then v = {} enddofile("scripts/inc_flags.lua")v.shake = 10v.t = 0function init(me)    local c = node_getContent(me)        if c and c ~= "" then        v.shake = tonumber(c)    endendfunction update(me, dt)    if v.t < dt then        if node_isEntityIn(me, getNaija()) then            shakeCamera(v.shake, 1)        end        v.t = 0.2    else        v.t = v.t - dt    endendfunction activate(me)endfunction songNote(me, note)endfunction songNoteDone(me, note, done)end