if not v then v = {} enddofile("scripts/inc_flags.lua")v.needinit = truev.txt = ""v.q = 0v.on = falsefunction init(me)    v.txt = node_getName(me)    v.on = true    node_setCursorActivation(me, v.on)endfunction update(me, dt)    if v.on then        if v.needinit then            v.needinit = false            v.q = createQuad("fish")            quad_scale(v.q, 2.5, 2.5)            quad_scale(v.q, 5, 5, 0.8, -1, 1)            quad_rotate(v.q, 360, 3, -1)        end                local x, y = node_getPosition(me)        quad_setPosition(v.q, x, y)    endendfunction activate(me)    if v.on then        setControlHint(v.txt, false, false, false, 5, "youngli/head-shock")        playSfx("bbbounce")    endendfunction songNote(me, note)endfunction songNoteDone(me, note, done)end